import 'package:flutter/material.dart';
import 'package:advanced_share/advanced_share.dart' show AdvancedShare;

void main() => runApp(new MyApp());

class MyApp extends StatefulWidget {
  @override
  _MyAppState createState() => new _MyAppState();
}

class _MyAppState extends State<MyApp> {
  @override
  initState() {
    super.initState();
  }

  void onPressed() {
    AdvancedShare.generic(
      msg: "Test1", 
      subject: "4444",
      title: "Payla≈ü",
      url:BASE64_IMAGE //Local veya Base64
      );
  }

  @override
  Widget build(BuildContext context) {
    return new MaterialApp(
      title: 'Advanced Share',
      home: new Scaffold(
        appBar: new AppBar(
          title: new Text('Advanced Share example app'),
        ),
        body: new Center(
          child: new IconButton(
            icon: new Icon(Icons.share),
            onPressed: onPressed,
          ),
        ),
      ),
    );
  }
}

const String BASE64_IMAGE = "data:image/png;base64,";
